import { AcademyTypeEnum } from './../../interfaces/academy-type.enum';
import { Component, OnInit } from '@angular/core';
import { Student } from '../../interfaces/student.interface';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent implements OnInit {
  developmentStudents: Student[] = [];
  designStudents: Student[] = [];
  devopsStudents: Student[] = [];
  qaStudents: Student[] = [];

  ngOnInit() {
    this.calculateTopStudents();
  }

  calculateTopStudents() {
    this.developmentStudents = this.calculateTopStudentsPerAcademy(
      AcademyTypeEnum.development
    );
    this.devopsStudents = this.calculateTopStudentsPerAcademy(
      AcademyTypeEnum.devops
    );
    this.designStudents = this.calculateTopStudentsPerAcademy(
      AcademyTypeEnum.design
    );
    this.qaStudents = this.calculateTopStudentsPerAcademy(AcademyTypeEnum.qa);
  }

  calculateTopStudentsPerAcademy(academy: AcademyTypeEnum) {
    return this.students
      .filter((student) => student.academy === academy)
      .sort((a, b) => {
        const aAverageGrade =
          a.grades.reduce((acc, grade) => acc + grade, 0) / a.grades.length;
        const bAverageGrade =
          b.grades.reduce((acc, grade) => acc + grade, 0) / b.grades.length;
        return bAverageGrade - aAverageGrade;
      })
      .slice(0, 3);
  }
}
